import log from 'loglevel';
/**
 * Ein Kunde Objekt erzeugen
 * @param kunde Json Objekt mit Daten vom RESTful WebServer
 * @returns Das initialisierte Kunde Objekt
 */

export const toKunde = (kundeServer, etag) => {
  let selfLink; // eslint-disable-next-line @typescript-eslint/naming-convention

  const {
    _links
  } = kundeServer;

  if (_links !== undefined) {
    const {
      self
    } = _links;
    selfLink = self.href;
  }

  let id;

  if (selfLink !== undefined) {
    const lastSlash = selfLink.lastIndexOf('/');
    id = selfLink.slice(lastSlash + 1);
  }

  let version;

  if (etag !== undefined) {
    const versionStr = etag.slice(1, -1);
    version = Number.parseInt(versionStr, 10);
  }

  const {
    nachname,
    email,
    kategorie,
    newsletter,
    geschlecht,
    adresse,
    username
  } = kundeServer;
  const kunde = {
    id,
    nachname,
    email,
    kategorie,
    newsletter,
    geschlecht,
    adresse: adresse !== null && adresse !== void 0 ? adresse : 'unbekannt',
    username,
    version
  };
  log.debug('Kunde.fromServer: kunde=', kunde);
  return kunde;
};
